#    ,    Base64
class Base64Image(Image):
    # '      IMAGE_DICT
    image_name = StringProperty('')

    #   ' KV-  
    def on_kv_post(self, base_widget):
        self.update_texture(IMAGE_DICT[self.image_name])

    # ,    image_name
    def on_image_name(self, instance, value):
        if value in IMAGE_DICT:
            self.update_texture(IMAGE_DICT[value])
        else:
            print(f" '{value}'  !")

    #  Base64-     
    def update_texture(self, encoded_image):
        decoded = base64.b64decode(encoded_image)
        buffer = BytesIO(decoded)
        self.texture = CoreImage(buffer, ext="png").texture


# -     hex   RGBA
class ColorConverter:
    @staticmethod
    def hex_to_rgba(hex_color, alpha=1):
        #  hex-    RGBA    0  1
        hex_color = hex_color.lstrip("#")
        r, g, b = int(hex_color[0:2], 16), int(hex_color[2:4], 16), int(hex_color[4:6], 16)
        return [round(r / 255.0, 4), round(g / 255.0, 4), round(b / 255.0, 4), alpha]


#         
class ThemeManager:
    light_theme = {
        'bg_color': ColorConverter.hex_to_rgba('#FCFCFC'),  #  
        'text_color': ColorConverter.hex_to_rgba('#1E1E1E'),  #  
        'button_bg': ColorConverter.hex_to_rgba('#EC9D75'),  #  
        'input_bg': ColorConverter.hex_to_rgba('#FCFCFC'),  #   
        'stroke_color': ColorConverter.hex_to_rgba('#1E1E1E'),  #  
        'logo_image': 'logo_light',  # '    
        'correct_color': ColorConverter.hex_to_rgba('#00C300'),  #   
        'partly_correct_color': ColorConverter.hex_to_rgba('#D4EF07'),  #    
        'incorrect_color': ColorConverter.hex_to_rgba('#B1B1B1'),  #   
        'victory_color': ColorConverter.hex_to_rgba('#00C300'),  #  
        'defeat_color': ColorConverter.hex_to_rgba('#FF0000'),  #  
        'left_arrow_image': 'left_arrow_light',  #  
        'right_arrow_image': 'right_arrow_light',  #  
        'changed_color': ColorConverter.hex_to_rgba('#C16565')  #    
    }
    
    dark_theme = {
        'bg_color': ColorConverter.hex_to_rgba('#1E1E1E'),
        'text_color': ColorConverter.hex_to_rgba('#FCFCFC'),
        'button_bg': ColorConverter.hex_to_rgba('#C16565'),
        'input_bg': ColorConverter.hex_to_rgba('#C16565'),
        'stroke_color': ColorConverter.hex_to_rgba('#FCFCFC'),
        'logo_image': 'logo_dark',
        'correct_color': ColorConverter.hex_to_rgba('#008000'),
        'partly_correct_color': ColorConverter.hex_to_rgba('#A1B907'),
        'incorrect_color': ColorConverter.hex_to_rgba('#808080'),
        'victory_color': ColorConverter.hex_to_rgba('#008000'),
        'defeat_color': ColorConverter.hex_to_rgba('#FF0000'),
        'left_arrow_image': 'left_arrow_dark',
        'right_arrow_image': 'right_arrow_dark',
        'changed_color': ColorConverter.hex_to_rgba('#EC9D75')
    }


# ,      
class ThemedLabel(Label):
    bg_color = ListProperty([1, 1, 1, 1])  #   

    def __init__(self, **kwargs):
        super().__init__(**kwargs)
        self.app = App.get_running_app()
        self.bg_color = self.app.theme['button_bg']  #   
        self.app.bind(theme=self.update_theme)  #    

    def update_theme(self, instance, value):
        #   ,   
        self.bg_color = value['button_bg']


#   
class DoodleWordMenu(Screen):
    #    
    def on_kv_post(self, base_widget):
        self.app = App.get_running_app()

    #    
    def go_to_settings(self):
        self.manager.current = 'settings'


#      ( )
class WordLengthSpinBox(BoxLayout):
    app = None  #   
    index = NumericProperty(1)  #     items
    items = ListProperty([])  #     

    def __init__(self, **kwargs):
        super().__init__(**kwargs)
        self.app = App.get_running_app()
        self.items = self.app.editions_names  #    

    #     ,  
    def next_element(self):
        if self.index + 1 >= len(self.items):
            return
        self.index += 1
        self.app.set_edition(self.items[self.index][0])
        self.app.root.get_screen('settings').changed_session_settings = True
        self.app.clear_user_word()

    #     ,  
    def prev_element(self):
        if self.index <= 0:
            return
        self.index -= 1
        self.app.set_edition(self.items[self.index][0])
        self.app.root.get_screen('settings').changed_session_settings = True
        self.app.clear_user_word()


#      ( )
class AttemptsSpinBox(BoxLayout):
    app = None  #   
    index = NumericProperty(5)  #     items
    items = ListProperty([])  #   

    def __init__(self, **kwargs):
        super().__init__(**kwargs)
        self.app = App.get_running_app()
        self.items = self.app.attempts_names  #    

    #    
    def next_element(self):
        if self.index + 1 >= len(self.items):
            return
        self.index += 1
        self.app.attempts = self.items[self.index][0]
        self.app.root.get_screen('settings').changed_session_settings = True

    #    
    def prev_element(self):
        if self.index <= 0:
            return
        self.index -= 1
        self.app.attempts = self.items[self.index][0]
        self.app.root.get_screen('settings').changed_session_settings = True


#      
class UserWordModal(ModalView):
    title = StringProperty(' ')  #  
    word_input = ObjectProperty(None)  #   
    info_label = ObjectProperty(None)  #    

    #    
    def on_kv_post(self, base_widget):
        self.app = App.get_running_app()

    #    info_label
    def set_info_label_color_for_time(self, color, time):
        self.info_label.color = color
        Clock.schedule_once(self.revert_hint_label_color, time)

    #     
    def revert_hint_label_color(self, dt):
        self.info_label.color = self.app.theme['text_color']

    # ,      
    def is_ukrainian_word(self, word):
        ukrainian_letters = set("'")
        for char in word:
            if char not in ukrainian_letters:
                return False
        return True

    # ,      
    def has_too_many_apostrophes(self, word):
        apostrophe_count = 0
        for char in word:
            if char == "'":
                apostrophe_count += 1
                if apostrophe_count > 1:
                    return True
        return False

    #     ,   ,  
    def write_user_word(self):
        if len(self.word_input.text) != self.app.edition['length']:
            #   
            if self.app.edition['length'] == 4:
                self.title = "   4 "
            else:
                self.title = f"   {self.app.edition['length']} "
            self.set_info_label_color_for_time(self.app.theme['defeat_color'], 0.5)
            return

        input_word = self.word_input.text.lower()

        if self.has_too_many_apostrophes(input_word):
            #    
            self.title = "   "
            self.set_info_label_color_for_time(self.app.theme['defeat_color'], 0.5)
            return

        if not self.is_ukrainian_word(input_word):
            #    
            self.title = "    "
            self.set_info_label_color_for_time(self.app.theme['defeat_color'], 0.5)
            return

        #   
        self.app.set_user_word(input_word)
        self.app.root.get_screen('settings').changed_session_settings = True

        #   
        self.dismiss()


class DoodleWordSettings(Screen):
    #    ,     config.ini
    changed_settings = False
    #    ,      ( )
    changed_session_settings = False

    #    
    def on_kv_post(self, base_widget):
        self.app = App.get_running_app()

    #   
    def change_setting(self, key, value):
        self.app.settings[key] = value
        self.changed_settings = True

    #      
    def switch_theme(self):
        if self.app.theme == ThemeManager.light_theme:
            self.app.theme = ThemeManager.dark_theme
            self.change_setting('theme', 'dark')
        elif self.app.theme == ThemeManager.dark_theme:
            self.app.theme = ThemeManager.light_theme
            self.change_setting('theme', 'light')

    #     config.ini
    def write_config(self):
        config = configparser.ConfigParser()
        config['Settings'] = self.app.settings
        this_file_dir = os.path.dirname(os.path.abspath(__file__))
        config_path = os.path.join(this_file_dir, 'config.ini')
        with open(config_path, 'w') as file:
            config.write(file)

    #       
    def open_user_word_modal(self):
        user_word_modal = UserWordModal()
        user_word_modal.open()

    #       
    def restart_game_button_action(self):
        self.changed_session_settings = False
        self.app.clear_user_word()
        self.restart_game()

    #  
    def restart_game(self):
        self.app.root.get_screen('game').game_restart()

    #    
    def back_to_menu(self):
        if self.changed_session_settings:
            #     ,  
            self.changed_session_settings = False
            self.restart_game()
        if self.changed_settings:
            #     ,    
            self.write_config()
        self.manager.current = 'menu'


